    public DataKelas() {
        this.conn = connection.getConn();
        initComponents();
         
        model = new DefaultTableModel();

        tabelkelas.setModel(model);
        model.addColumn("ID Kelas");
        model.addColumn("Nama Kelas");
        model.addColumn("Wali Kelas");
        model.addColumn("NIP Guru");

        loadData();
    }
    
     public final void loadData() {
        btSimpan.setEnabled(true);
        btHapus.setEnabled(true);
        btEdit.setEnabled(false);
        model.getDataVector().removeAllElements();
        model.fireTableDataChanged();
                 
            try {
                Connection c = connection.getConn();
            try (Statement s = c.createStatement()) {
                String sql = "SELECT * FROM kelas";
                    try (ResultSet rs = s.executeQuery(sql)) {
                        while (rs.next()) {
                            Object[] o = new Object[4];
                            o[0] = rs.getString("id_kelas");
                            o[1] = rs.getString("namakelas");
                            o[2] = rs.getString("walikelas");
                            o[3] = rs.getString("nipguru");
                                                        
                            model.addRow(o);
                        }   }
            }
        } catch (SQLException e) {
            JOptionPane.showMessageDialog(this, "ERROR : \n" + e.toString(), "Kesalahan", JOptionPane.ERROR_MESSAGE);
        }
   }
    
       public void kosong() {
        tkode.setText(null);
        tkelas.setText(null);
        twali.setText(null);
        tnip.setText(null);
        tcari.setText(null);

        tkode.requestFocus();
    }
       
       public void aktif() {
        tkode.setEnabled(true);
        tkelas.setEnabled(true);
        twali.setEnabled(true);
        tnip.setEnabled(true);
        
        btSimpan.setEnabled(false);     
    }
    
    
     public void carikelas(){
        String cari = tcari.getText();
        //String to=jCombo.getItemAt(i).toString();
        
       Object[] Baris={"ID Kelas","Nama Kelas","Wali Kelas","NIP Guru"};
       model = new DefaultTableModel(null, Baris);
       tabelkelas.setModel(model);

       try{
           String sql="SELECT * from kelas WHERE "
                   + "id_kelas like '%"+cari+"%' "
                   + "OR namakelas like '%"+cari+"%' "
                   + "OR walikelas like '%"+cari+"%' "
                   + "OR nipguru like '%"+cari+"%' " 
                   + "order by id_kelas asc";
           java.sql.Statement stmt=conn.createStatement();
           java.sql.ResultSet rslt=stmt.executeQuery(sql);
           while(rslt.next()){
               String id=rslt.getString("id_kelas");
               String namakls=rslt.getString("namakelas");
               String wakel=rslt.getString("walikelas");
               String nip=rslt.getString("nipguru");
  
               String[] dataField={id,namakls,wakel,nip};
               model.addRow(dataField);
           }
       }
       catch(SQLException e){
           JOptionPane.showMessageDialog(this, "ERROR : \n" + e.toString(), "Kesalahan", JOptionPane.ERROR_MESSAGE);
       }
    }

