    private void btEditActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btEditActionPerformed
        // TODO add your handling code here:
         try{
            String sql="UPDATE kelas SET namakelas=? ,walikelas=?, nipguru=? where id_kelas='"+tkode.getText()+"'";
            PreparedStatement stat=conn.prepareStatement(sql);
            
        stat.setString(1, tkelas.getText());
        stat.setString(2, twali.getText());
        stat.setString(3, tnip.getText());

        stat.executeUpdate();
        
            JOptionPane.showMessageDialog(null, "Data Berhasil Diubah");
            kosong();
            aktif();
            loadData();
            btSimpan.setEnabled(true);
            
        } catch (SQLException e) {
            JOptionPane.showMessageDialog(null, "Data Gagal Diubah"+e);
        }        
    }//GEN-LAST:event_btEditActionPerformed

    private void tabelkelasMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_tabelkelasMouseClicked
        // TODO add your handling code here:
        btEdit.setEnabled(true);
        int i = tabelkelas.getSelectedRow();
        if (i == -1) {
            return;
        }

        String kd = (String) model.getValueAt(i, 0);
        tkode.setText(kd);

        String nmkls = (String) model.getValueAt(i, 1);
        tkelas.setText(nmkls);
        
        String wali = (String) model.getValueAt(i, 2);
        twali.setText(wali);
        
        String nip = (String) model.getValueAt(i, 3);
        tnip.setText(nip);
    }//GEN-LAST:event_tabelkelasMouseClicked

    private void btSimpanActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btSimpanActionPerformed
        // TODO add your handling code here:
         if(tkode.getText().equals("") ||tkelas.getText().equals("") || twali.getText().equals("")|| tnip.getText().equals("")) {
            JOptionPane.showMessageDialog(null, "LENGKAPI DATA !", "SDIT Bahrul Fikri", JOptionPane.INFORMATION_MESSAGE);
         }else{          
            String kode = tkode.getText();
            String kls = tkelas.getText();
            String wali = twali.getText();
            String nip = tnip.getText();

            try {
                String sql = "INSERT INTO kelas VALUES (?, ?, ?, ?)";

                try (PreparedStatement p = conn.prepareStatement(sql)) {
                    p.setString(1, kode);
                    p.setString(2, kls);
                    p.setString(3, wali);
                    p.setString(4, nip);
                                       
                    p.executeUpdate();
                }
            } catch (SQLException e) {
                JOptionPane.showMessageDialog(this, "ERROR : \n" + e.toString(), "Kesalahan", JOptionPane.ERROR_MESSAGE);
            } finally {
                loadData();
                kosong();
                aktif();
                JOptionPane.showMessageDialog(null, "Data Berhasil Tersimpan", "SDIT Bahrul Fikri", JOptionPane.INFORMATION_MESSAGE);
            }
        }
    }//GEN-LAST:event_btSimpanActionPerformed

    private void btHapusActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btHapusActionPerformed
        // TODO add your handling code here:
        int confirm = JOptionPane.showConfirmDialog(null, "Ingin Menghapus Data?", "Konfirmasi", JOptionPane.YES_NO_OPTION);

        if(confirm==JOptionPane.YES_OPTION) {

            try {
                String sql ="DELETE FROM kelas WHERE id_kelas='"+tkode.getText()+"'";

                java.sql.PreparedStatement ps=conn.prepareStatement(sql);
                ps.execute();
                JOptionPane.showMessageDialog(this, "Data Berhasil Dihapus");
            } catch (HeadlessException | SQLException e) {
                JOptionPane.showMessageDialog(this, e.getMessage());
            }
            loadData();
            kosong();
            aktif();
        }
    }//GEN-LAST:event_btHapusActionPerformed

