    private void previewReport(String filename, HashMap parameter, String jenis) {
        try {
            File fileReport = new File("src\\laporan\\" + filename);
            JasperReport jasperReport = (JasperReport) JRLoader.loadObject(fileReport.getPath());
            parameter.put("tgl1", tgl1.getDate());
            parameter.put("tgl2", tgl2.getDate());
            JasperPrint jasperPrint = JasperFillManager.fillReport(jasperReport, parameter, connection.getConn());
            JasperViewer.viewReport(jasperPrint, false);

        } catch (JRException e) {
            JOptionPane.showMessageDialog(null, "Error " + e);
            System.err.println("Error " + e);
        }
    }
