    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        if (tgl1.getDate().equals("") || tgl2.getDate().equals("")) {
            JOptionPane.showMessageDialog(null, "Masukkan Tanggal Terlebih Dahulu!", "SDIT Bahrul Fikri", JOptionPane.INFORMATION_MESSAGE);
        } else {
            HashMap parameter = new HashMap();
            //boolean isError = false;
            int ok = JOptionPane.showConfirmDialog(null, "Ingin Mencetak Laporan?", "Konfirmasi", JOptionPane.YES_NO_OPTION);

            if /*(!isError)*/ (ok == 0) {
                String LP = cbReport.getSelectedItem().toString();
                switch (LP) {
                    case "Pembayaran Siswa":
                        previewReport("laporanpembayaran.jasper", parameter, LP);
                        break;
                    case "Pendapatan Non Siswa":
                        previewReport("laporanpendapatannonsiswa.jasper", parameter, LP);
                        break;
                    case "Pengeluaran Sekolah":
                        previewReport("laporanpengeluaransekolah.jasper", parameter, LP);
                        break;
                    case "Surat Masuk":
                        previewReport("laporansuratmasuk.jasper", parameter, LP);
                        break;
                    case "Surat Keluar":
                        previewReport("laporansuratkeluar.jasper", parameter, LP);
                        break;
                    default:
                        JOptionPane.showMessageDialog(null, "Anda belum memilih laporan!");
                        break;
                }
            } else {
            }
        }
    }//GEN-LAST:event_jButton1ActionPerformed

