    private void tampil(){
        txId.setText(String.valueOf(id));
        txUsername.setText(username);
        txPass.setText(password);
        txNama.setText(nama);
        txAlamat.setText(alamat);
        txNotelp.setText(telepon);
        txEmail.setText(email);
        lblUserLogin.setText("Login sebagai: " +username);
    }
    
    private void tampildata(){
     try {
                String sql = "UPDATE admin SET nama = '" + txNama.getText() + "', password = '"+ txPass.getText() + "', "
                        + "alamat = '"+txAlamat.getText()+ "', notelp = '"+txNotelp.getText()+ "', email = '"+txEmail.getText()+"', posisi WHERE  id ='" + txId.getText() + "'";
                PreparedStatement p = conn.prepareStatement(sql);
                p.executeUpdate();
                p.close();
            } catch (SQLException e) {
                System.out.println("Terjadi Error");
            } finally {
                 try {
                    String sql = "SELECT * FROM admin WHERE username = '" + txUsername.getText() + "' AND id = '" + txId.getText() + "'";
                    Statement st = conn.createStatement();
                    ResultSet rs = st.executeQuery(sql);
                    while (rs.next()) {
                    txId.setText(rs.getString("id"));
                    txUsername.setText(rs.getString("username"));
                    txPass.setText(rs.getString("password"));
                    txNama.setText(rs.getString("nama"));
                    txAlamat.setText(rs.getString("alamat"));
                    txNotelp.setText(rs.getString("notelp"));
                    txEmail.setText(rs.getString("email"));
            }    
            
        } catch (SQLException ex) {
            Logger.getLogger(Profil.class.getName()).log(Level.SEVERE, null, ex);
        }
                off();
                btEdit.setEnabled(true);
                btSimpan.setEnabled(false);
                JOptionPane.showMessageDialog(null, "Data Berhasil Diubah", "SDIT Bahrul Fikri", JOptionPane.INFORMATION_MESSAGE);
            }
}
    
    protected void aktif(){
        txId.setEnabled(false);
        txUsername.setEnabled(false);
        txPass.setEditable(true);
        txNama.setEditable(true);
        txAlamat.setEditable(true);
        txNotelp.setEditable(true);
        txEmail.setEditable(true);
    }
    
    protected void off() {
        txId.setEditable(false);
        txUsername.setEditable(false);
        txPass.setEditable(false);
        txNama.setEditable(false);
        txAlamat.setEditable(false);
        txNotelp.setEditable(false);
        txEmail.setEditable(false);
    }
    
